pragma solidity ^0.5.0;

contract iSeeYouList {
    uint256 public taskCount = 0;

    struct Message {
        uint256 id;
        string from;
        string to;
        string message;
        int256 size;
    }

    mapping(uint256 => Message) public messages;

    constructor() public {
        createMessage("Juan", "Lily", "We did it!", 5);
    }

    function createMessage(
        string memory _from,
        string memory _to,
        string memory _message,
        int256 _size
    ) public {
        taskCount++;
        messages[taskCount] = Message(taskCount, _from, _to, _message, _size);
    }
}
